mapper.select:
select tc.clue_id,tc.name,tch.channel_name,tc.phone,tc.status,tc.create_time,tc.create_by,ta.user_name as
owner,tc.next_time from tienchin_clue tc left join tienchin_channel tch on tc.channel_id=tch.channel_id left
join tienchin_assignment ta on tc.clue_id=ta.assign_id and ta.latest=true where tc.del_flag=0 and ta.type=0

    and tc.name=#{name}


    and tc.phone=#{phone}


    and tc.channel_id=#{channelId}


    and tc.status=#{status}


    and ta.user_name=#{owner}


    and tc.next_time > #{params.beginTime} and tc.next_time < #{params.endTime}

select t2.date_str as x,if(t3.c is null,0,t3.c) as y from( select @sdate:=date_add(@sdate,interval 1 day) as date_str from( select @sdate:=date_add(date_format(#{params.beginTime},'%Y-%m-%d'),interval -1 day) from mysql.time_zone limit 999) t1) t2 left join (select date_format(create_time,'%Y-%m-%d') as date_str,count(1) as c from tienchin_clue
and channel_id=#{channelId}
and status=#{status}
group by date_format(create_time,'%Y-%m-%d')) t3 on t2.date_str=t3.date_str where date(@sdate) <= date_add(date_format(#{params.endTime},'%Y-%m-%d'),interval -1 day)

set @cnum:=0;
select t4.x,(@cnum:=@cnum+t4.y) as y from(
select t2.date_str as x,if(t3.c is null,0,t3.c) as y from(
select @sdate:=date_add(@sdate,interval 1 day) as date_str from(
select @sdate:=date_add(date_format(#{params.beginTime},'%Y-%m-%d'),interval -1 day) from mysql.time_zone limit 999) t1) t2 left join (select
date_format(create_time,'%Y-%m-%d') as date_str,count(1) as c from tienchin_clue


        and channel_id=#{channelId}


        and status=#{status}


group by date_format(create_time,'%Y-%m-%d')) t3 on t2.date_str=t3.date_str where date(@sdate) <=
date_add(date_format(#{params.endTime},'%Y-%m-%d'),interval -1 day)) t4;
